var input = ""
// input = "02935109699940807407585447034323"
input = input.toString().split('');
function generate_pattern(pos, len){
	var value = [];
	var index = 0;
	while(len>index){
		for(var j=0;j<pos;j++){
			value.push(0);
			index++;
		}
		for(var j=0;j<pos;j++){
			value.push(1)
			index++;
		}
		for(var j=0;j<pos;j++){
			value.push(0)
			index++;
		}
		for(var j=0;j<pos;j++){
			value.push(-1)
			index++;
		}
	}
	value.push(value.splice(0,1)[0]);
	return value.splice(0, len);
}

function multiply(num, base){
	var sum = 0;
	for(var i=0;i<base.length;i++){
		sum += parseInt(num[i])*base[i]
	}
	return Math.abs(sum)%10;
}

var finale = "";
for(var phase=0;phase<100;phase++){
	finale="";
	input.forEach(function(item, index){
		var base = generate_pattern(index+1, input.length);
		finale+=multiply(input, base);
	})
	input=finale.split('');
}
console.log('part1: ', finale.substr(0, 8));
input = ""
var offset = parseInt(input.slice(0, 7))
input = input.repeat(10000);
console.log(input.length/2 < offset)
input = input.toString().split('').map(function(aa){
	return parseInt(aa);
});
var finale2 = [];
var initial_sum = 0;
for(var phase=0;phase<100;phase++){
	current = input.slice(offset, input.length)
	initial_sum = current.reduce((a,b) => a+b)
	finale2.push(initial_sum%10)
	for(var start=offset+1; start<input.length; start++){
		initial_sum -= current.splice(0,1)[0];
		finale2.push(Math.abs(initial_sum)%10)
	}
	console.log('phase: ', phase, ':' ,finale2.join('').slice(0,8));
	input = [...input.slice(0, offset), ...finale2];
	finale2 = [];
}
console.log('part2: ', finale2.join('').slice(0,8));





















